	InstallKeyboardHandler(MyDisplayHandler, "Step Simulation", "If the simulation is paused, step forward .1 sec.", kNoModifier, 'o');
	InstallKeyboardHandler(MyDisplayHandler, "Change weight", "Change the search weight", kNoModifier, 'w');
	InstallKeyboardHandler(MyDisplayHandler, "Map 0", "Map 0", kNoModifier, '0');
	InstallKeyboardHandler(MyDisplayHandler, "Map 1", "Map 1", kNoModifier, '1');
	InstallKeyboardHandler(MyDisplayHandler, "Map 2", "Map 2", kNoModifier, '2');
	InstallKeyboardHandler(MyDisplayHandler, "Map 3", "Map 3", kNoModifier, '3');
	InstallKeyboardHandler(MyDisplayHandler, "Map 4", "Map 4", kNoModifier, '4');
	InstallKeyboardHandler(MyDisplayHandler, "Sector Size 8", "Set Sector Size to 8x8", kNoModifier, '8');
	InstallKeyboardHandler(MyDisplayHandler, "Sector Size 16", "Set Sector Size to 16x16", kNoModifier, '9');
	InstallKeyboardHandler(MyDisplayHandler, "Rotate Compression", "Rotate Compression being shown in heuristic", kAnyModifier, '}');
	InstallKeyboardHandler(MyDisplayHandler, "Rotate Displayed Heuristic", "Rotate which heuristic is shown", kAnyModifier, '{');
