void MyWindowHandler(unsigned long windowID, tWindowEventType eType)
{
	if (eType == kWindowDestroyed)
	{
		printf("Window %ld destroyed\n", windowID);
		RemoveFrameHandler(MyFrameHandler, windowID, 0);
		
		runningSearch1 = false;
		mouseTracking = false;
	}
	else if (eType == kWindowCreated)
	{
		printf("Window %ld created\n", windowID);
		InstallFrameHandler(MyFrameHandler, windowID, 0);
		ReinitViewports(windowID, {-1, -1, 1, 1}, kScaleToSquare);

		LoadMap(whichMap);
	}
	
}

void MyFrameHandler(unsigned long windowID, unsigned int viewport, void *)
{
	Graphics::Display &display = getCurrentContext()->display;

	if (mapChanged == true)
	{
		display.StartBackground();
		me->Draw(display);
		msa->Draw(display);
		display.EndBackground();
		mapChanged = false;
	}

	if (mouseTracking)
	{
		me->SetColor(Colors::red);
		me->DrawLine(display,xyLoc(px1, py1), xyLoc(px2, py2));
	}
	
	if (runningSearch1)
	{
		for (int x = 0; x < gStepsPerFrame; x++)
		{
			if (astar.DoSingleSearchStep(abstractPath))
			{
//					printf("Solution: moves %d, length %f, %lld nodes, %u on OPEN\n",
//						   (int)path.size(), ma1->GetPathLength(path), a1.GetNodesExpanded(),
//						   a1.GetNumOpenItems());
				runningSearch1 = false;
				break;
			}
		}
	}
	if (runningSearch1 || abstractPath.size() > 0)
		astar.Draw(display);

	if (abstractPath.size() != 0)
	{
		msa->SetColor(Colors::green);
		for (int x = 1; x < abstractPath.size(); x++)
		{
			msa->DrawLine(display, abstractPath[x-1], abstractPath[x], 5);
		}

	}
}



void MyDisplayHandler(unsigned long windowID, tKeyboardModifier mod, char key)
{
	char messageString[255];
	switch (key)
	{
		case '|': resetCamera(); break;
		case 'r': recording = !recording; break;
		case '8': sectorSize = 8; LoadMap(whichMap); break;
		case '9': sectorSize = 16; LoadMap(whichMap); break;
		case '0':
		case '1':
		case '2':
		case '3':
		case '4':
			whichMap = key-'0';
			LoadMap(whichMap);
			break;
		case '[':
			gStepsPerFrame /= 2;
			sprintf(messageString, "Speed: %d steps per frame\n", gStepsPerFrame);
			submitTextToBuffer(messageString);
			break;
		case ']':
			if (gStepsPerFrame <= 16384)
				gStepsPerFrame *= 2;
			if (gStepsPerFrame == 0)
				gStepsPerFrame = 1;
			sprintf(messageString, "Speed: %d steps per frame\n", gStepsPerFrame);
			submitTextToBuffer(messageString);
			break;
		case '{':
			break;
		case '}':
			break;
		case '\t':
			break;
//		case 'p': unitSims[windowID]->SetPaused(!unitSims[windowID]->GetPaused()); break;
		case 'o':
		{
			if (abstractPath.size() == 0)
				if (astar.DoSingleSearchStep(abstractPath))
				{
					runningSearch1 = false;
					break;
				}
		}
			break;
		case 'q':
		{
		}
		default:
			break;
	}
}

void StartSearch()
{
	astar.SetStopAfterGoal(true);
	xyLoc s1;
	xyLoc g1;
	s1.x = px1; s1.y = py1;
	g1.x = px2; g1.y = py2;
	abstractGridState start, goal;
	start = msa->GetAbstractState(s1);
	goal = msa->GetAbstractState(g1);
	
	astar.InitializeSearch(msa, start, goal, abstractPath);
	mouseTracking = false;
	runningSearch1 = true;
}

bool MyClickHandler(unsigned long windowID, int, int, point3d loc, tButtonType button, tMouseEventType mType)
{
	if (button == kLeftButton)
	{
		switch (mType)
		{
			case kMouseDown:
			{
				int x, y;
				me->GetMap()->GetPointFromCoordinate(loc, x, y);
				if ((me->GetMap()->GetTerrainType(x, y)>>terrainBits) != (kGround>>terrainBits))
					break;

				px1 = px2 = x;
				py1 = py2 = y;
				path.resize(0);
				mouseTracking = true;
			}
				break;
			case kMouseDrag:
			{
				if (!mouseTracking)
					break;
				int x, y;
				me->GetMap()->GetPointFromCoordinate(loc, x, y);
				if ((me->GetMap()->GetTerrainType(x, y)>>terrainBits) == (kGround>>terrainBits))
				{
					px2 = x;
					py2 = y;
				}
			}
				break;
			case kMouseUp:
			{
				if (!mouseTracking)
					break;
				int x, y;
				me->GetMap()->GetPointFromCoordinate(loc, x, y);
				if ((me->GetMap()->GetTerrainType(x, y)>>terrainBits) == (kGround>>terrainBits))
				{
					px2 = x;
					py2 = y;
				}
				StartSearch();
			}
				break;
		}
		return true;
	}
	return false;
}




