void LoadMap(int which)
{
	if (me)
		delete me->GetMap();
	delete me;
	delete msa;
	mapChanged = true;
	runningSearch1 = false;
	mouseTracking = false;
	abstractPath.clear();
	astar.SetHeuristic(0);
	Map *m = new Map(1, 1);

	switch(which)
	{
		case 0:BGMap2(m); break;
		case 1:LoadMap0(m); break;
		case 2:LoadMap1(m); break;
		case 3:LoadMap2(m); break;
		case 4:LoadMap3(m); break;

	}

	me = new MapEnvironment(m);
	msa = new Map2DSectorAbstraction(m, sectorSize);
}
